function isPalindrome(str) {
    let rev = str.split("").reverse().join("");
    return str == rev;
}

function longestPalindrome(str) {
    let maxLength = 0;
    let result = '';

    for (let i = 0; i < str.length; i++) {
        let subs = str.substr(i, str.length);

        for (let j = subs.length; j >= 0; j--) {
            let subSubsStr = subs.substr(0, j);
            if (subSubsStr.length <= 1)
                continue;

            if (isPalindrome(subSubsStr)) {
                if (subSubsStr.length > maxLength) {
                    maxLength = subSubsStr.length;
                    result = subSubsStr;
                }
            }
        }
    }

    return result.length == 0 ? str : result;
}

